class XMLParser {
  constructor(options = {}) {
    this.options = options;
  }

  parse(input) {
    if (typeof input === 'string') {
      try {
        return JSON.parse(input);
      } catch {
        return {};
      }
    }
    if (Buffer.isBuffer(input)) {
      try {
        return JSON.parse(input.toString('utf8'));
      } catch {
        return {};
      }
    }
    return input ?? {};
  }
}

module.exports = { XMLParser };
